mapper.select:
SELECT COUNT(1) FROM `event_type` WHERE 1=1
and name LIKE CONCAT('%', #{keyword}, '%')
and parent_id = #{parentId}

SELECT `id`, `name`, `lft`, `rht`, `parent_id` AS `parentId` FROM `event_type`
and `name` LIKE CONCAT('%', #{keyword}, '%')
and `parent_id` = #{parentId}
ORDER BY `lft`
LIMIT #{startNum}, #{pageSize}

SELECT a.`id`, COUNT(DISTINCT b.`solution_id`) AS solutionCount, COUNT(DISTINCT c.`id`) AS childCount FROM `event_type` a LEFT JOIN `event_type_solution` b ON b.`event_type_id` = a.`id` LEFT JOIN `event_type` c ON c.`parent_id` = a.`id` WHERE a.`id` IN
#{eventTypeId}
GROUP BY a.`id`

select
b.`event_type_id`,
b.`type`,
b.`uuid`
FROM `event_type` a
LEFT JOIN `event_type_authority` b on a.`id` = b.`event_type_id`
WHERE a.`lft` > #{lft} AND a.`rht` < #{rht}
AND `type` = #{authorityVo.type}
AND `uuid` = #{authorityVo.uuid}
