select count(1) from (
SELECT a.`uuid` FROM `user` a JOIN `user_session` b ON b.`user_uuid` = a.`uuid` AND b.`visit_time` > #{sessionTime} WHERE `uuid` IN
#{userUuid}
UNION
SELECT a.`user_uuid` FROM `user_role` a JOIN `user_session` b ON b.`user_uuid` = a.`user_uuid` AND b.`visit_time` > #{sessionTime} WHERE `role_uuid` IN
#{roleUuid}
UNION
SELECT a.`user_uuid` FROM `user_team` a JOIN `user_session` b ON b.`user_uuid` = a.`user_uuid` AND b.`visit_time` > #{sessionTime} WHERE `team_uuid` IN
#{teamUuid}
) a

